                readStream.pipe(response);
                db.collection('logs').insert({ sigla: sigla, ip: remoteIP, referer: referer, op: urlTocFile + '/' + tocFileName + uriPartTocPdf, state: 'local', keywords: [], timestamp: timestamp }, { w: 0 });
